// client/src/components/MyBookings.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './MyBookings.css'; // Assuming you have a CSS file for this component

const MyBookings = () => {
    const [roomBookings, setRoomBookings] = useState([]);
    const [transferBookings, setTransferBookings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchBookings = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                setLoading(false);
                setError('You are not logged in.');
                return;
            }

            try {
                // Fetch room bookings
                const roomResponse = await axios.get('http://localhost:5000/api/bookings/mybookings', {
                    headers: { 'x-auth-token': token },
                });
                setRoomBookings(roomResponse.data);

                // Fetch transfer bookings
                const transferResponse = await axios.get('http://localhost:5000/api/private-transfers/mytransfers', {
                    headers: { 'x-auth-token': token },
                });
                setTransferBookings(transferResponse.data);

                setLoading(false);
            } catch (err) {
                console.error(err);
                setError('Failed to fetch bookings. Please try again.');
                setLoading(false);
            }
        };

        fetchBookings();
    }, []);

    if (loading) {
        return <div className="loading-container">Loading your bookings...</div>;
    }

    if (error) {
        return <div className="error-container">{error}</div>;
    }

    return (
        <div className="my-bookings-page-container">
            <h1 className="bookings-header">My Bookings</h1>

            {/* Room Bookings Section */}
            <div className="bookings-section">
                <h2>Room Bookings</h2>
                {roomBookings.length === 0 ? (
                    <p className="no-bookings">You have no room bookings.</p>
                ) : (
                    <div className="bookings-grid">
                        {roomBookings.map((booking) => (
                            <div key={booking._id} className="booking-card card">
                                <h3>{booking.room.title}</h3>
                                <p><strong>Check-in:</strong> {new Date(booking.checkInDate).toLocaleDateString()}</p>
                                <p><strong>Check-out:</strong> {new Date(booking.checkOutDate).toLocaleDateString()}</p>
                                <p><strong>Guests:</strong> {booking.guests}</p>
                                <p><strong>Total Price:</strong> BDT {booking.totalPrice}</p>
                                <Link to={`/rooms/${booking.room._id}`} className="btn-view-details">View Room</Link>
                            </div>
                        ))}
                    </div>
                )}
            </div>

            {/* Transportation Bookings Section */}
            <div className="bookings-section">
                <h2>Transportation Bookings</h2>
                {transferBookings.length === 0 ? (
                    <p className="no-bookings">You have no transportation bookings.</p>
                ) : (
                    <div className="bookings-grid">
                        {transferBookings.map((transfer) => (
                            <div key={transfer._id} className="booking-card card">
                                <h3>{transfer.transferType}</h3>
                                <p><strong>Arrival Date:</strong> {new Date(transfer.arrivalDate).toLocaleDateString()}</p>
                                <p><strong>Arrival Time:</strong> {transfer.arrivalTime}</p>
                                <p><strong>Round Trip:</strong> {transfer.isRoundTrip ? 'Yes' : 'No'}</p>
                                <p><strong>Total Price:</strong> BDT {transfer.totalPrice}</p>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default MyBookings;
