// client/src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import LandingPage from './pages/LandingPage';
import Auth from './components/Auth';
import HomePage from './pages/HomePage'; // Import the new HomePage
import './App.css'; 

// This is a new component to handle private routes
const PrivateRoutes = ({ isAuthenticated }) => {
  return isAuthenticated ? <HomePage /> : <Auth />;
};

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));

  return (
    <Router>
      <div className="main-container">
        <Routes>
          <Route path="/" element={<LandingPage />} />
          <Route path="/login" element={<Auth setIsAuthenticated={setIsAuthenticated} />} />
          <Route path="/home" element={<PrivateRoutes isAuthenticated={isAuthenticated} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
