// client/src/components/MyBookings.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ReviewForm from './ReviewForm';
import '../App.css';

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [reviews, setReviews] = useState({});
  const [showReviewForm, setShowReviewForm] = useState(null);
  const [popupMessage, setPopupMessage] = useState('');
  const [isPopupVisible, setIsPopupVisible] = useState(false);

  const showPopup = (message) => {
    setPopupMessage(message);
    setIsPopupVisible(true);
    setTimeout(() => {
      setIsPopupVisible(false);
    }, 3000);
  };

  const fetchReviewsForBookings = async (bookingsData) => {
    const allReviews = {};
    for (const booking of bookingsData) {
      if (booking.roomId) {
        try {
          const response = await axios.get(`http://localhost:5000/api/reviews/${booking.roomId._id}`);
          allReviews[booking.roomId._id] = response.data;
        } catch (err) {
          console.error(`Failed to fetch reviews for room ${booking.roomId._id}:`, err);
        }
      }
    }
    setReviews(allReviews);
  };

  const fetchMyBookings = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('You are not logged in.');
      setLoading(false);
      return;
    }

    try {
      const response = await axios.get('http://localhost:5000/api/bookings/mybookings', {
        headers: {
          'x-auth-token': token,
        },
      });
      setBookings(response.data);
      await fetchReviewsForBookings(response.data);
    } catch (err) {
      setError('Failed to fetch bookings.');
      console.error('Error fetching bookings:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchMyBookings();
  }, []);

  const onReviewSubmitted = (roomId) => {
    fetchMyBookings();
    setShowReviewForm(null);
  };

  const handleCancelBooking = async (bookingId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      showPopup('You must be logged in to cancel a booking.');
      return;
    }
    
    try {
      await axios.delete(`http://localhost:5000/api/bookings/${bookingId}`, {
        headers: {
          'x-auth-token': token,
        },
      });
      showPopup('Booking canceled successfully!');
      fetchMyBookings(); // Refresh the list of bookings
    } catch (err) {
      showPopup(err.response.data.msg || 'Failed to cancel booking.');
    }
  };

  if (loading) {
    return <div className="text-center loading-message">Loading your bookings...</div>;
  }

  if (error) {
    return <div className="text-center error-message">{error}</div>;
  }

  return (
    <div className="container my-bookings-container">
      <h2 className="my-bookings-title">My Bookings</h2>
      {bookings.length > 0 ? (
        <div className="bookings-grid">
          {bookings.map(booking => (
            <div key={booking._id} className="booking-card card">
              <img src={booking.roomId?.image} alt={booking.roomId?.title} className="booking-card-image" />
              <div className="booking-card-content">
                <h4 className="booking-card-title">{booking.roomId?.title}</h4>
                <p><strong>Check-in:</strong> {new Date(booking.checkInDate).toLocaleDateString()}</p>
                <p><strong>Check-out:</strong> {new Date(booking.checkOutDate).toLocaleDateString()}</p>
                <p><strong>Total Price:</strong> BDT {booking.totalPrice}</p>
                <div className="booking-actions">
                  <button 
                    className="btn-review"
                    onClick={() => setShowReviewForm(booking.roomId?._id)}
                  >
                    Write a Review
                  </button>
                  <button 
                    className="btn-cancel"
                    onClick={() => handleCancelBooking(booking._id)}
                  >
                    Cancel Booking
                  </button>
                </div>
                
                {showReviewForm === booking.roomId?._id && (
                  <ReviewForm 
                    roomId={booking.roomId?._id} 
                    onReviewSubmitted={() => onReviewSubmitted(booking.roomId?._id)} 
                  />
                )}
                
                {reviews[booking.roomId?._id] && (
                  <div className="reviews-section">
                    <h5>Your Review:</h5>
                    {reviews[booking.roomId._id].length > 0 ? (
                      reviews[booking.roomId._id].map(review => (
                        <div key={review._id} className="review-item">
                          <p><strong>{review.userId.username}</strong> rated it {review.rating}/5</p>
                          <p>"{review.comment}"</p>
                        </div>
                      ))
                    ) : (
                      <p>No reviews yet.</p>
                    )}
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-bookings-found">
          <p>You have no bookings yet. Go explore our rooms!</p>
        </div>
      )}
      {isPopupVisible && (
        <div className="popup-container">
          <div className="popup-message">{popupMessage}</div>
        </div>
      )}
    </div>
  );
};

export default MyBookings;
