// client/src/components/MyBookings.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ReviewForm from './ReviewForm';
import '../App.css'; // Ensure this is correctly linked

const MyBookings = () => {
    const [bookings, setBookings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [reviews, setReviews] = useState({});
    const [showReviewForm, setShowReviewForm] = useState(null);
    const [isPopupVisible, setIsPopupVisible] = useState(false);
    const [popupMessage, setPopupMessage] = useState('');

    const showPopup = (message) => {
        setPopupMessage(message);
        setIsPopupVisible(true);
        setTimeout(() => {
            setIsPopupVisible(false);
        }, 3000);
    };

    const fetchReviewsForBookings = async (bookingsData) => {
        const allReviews = {};
        for (const booking of bookingsData) {
            if (booking.roomId) {
                try {
                    const response = await axios.get(`http://localhost:5000/api/reviews/${booking.roomId._id}`);
                    allReviews[booking.roomId._id] = response.data;
                } catch (err) {
                    console.error(`Failed to fetch reviews for room ${booking.roomId._id}:`, err);
                }
            }
        }
        setReviews(allReviews);
    };

    const fetchMyBookings = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            setError('You are not logged in.');
            setLoading(false);
            return;
        }

        try {
            const roomBookingsResponse = await axios.get('http://localhost:5000/api/bookings/mybookings', {
                headers: { 'x-auth-token': token },
            });

            const transferBookingsResponse = await axios.get('http://localhost:5000/api/private-transfers/mytransfers', {
                headers: { 'x-auth-token': token },
            });
            
            const allBookings = [
                ...roomBookingsResponse.data.map(b => ({ ...b, type: 'room' })),
                ...transferBookingsResponse.data.map(b => ({ ...b, type: 'transfer' })),
            ];

            setBookings(allBookings);
            await fetchReviewsForBookings(roomBookingsResponse.data);
        } catch (err) {
            setError('Failed to fetch bookings.');
            console.error('Error fetching bookings:', err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchMyBookings();
    }, []);

    const onReviewSubmitted = (roomId) => {
        fetchMyBookings();
        setShowReviewForm(null);
    };

    const handleCancelBooking = async (bookingId) => {
        const token = localStorage.getItem('token');
        if (!token) {
            showPopup('You must be logged in to cancel a booking.');
            return;
        }
        
        try {
            await axios.delete(`http://localhost:5000/api/bookings/${bookingId}`, {
                headers: { 'x-auth-token': token },
            });
            showPopup('Room booking canceled successfully!');
            fetchMyBookings();
        } catch (err) {
            showPopup(err.response.data.msg || 'Failed to cancel room booking.');
        }
    };

    const handleCancelTransfer = async (transferId) => {
        const token = localStorage.getItem('token');
        if (!token) {
            showPopup('You must be logged in to cancel a transfer.');
            return;
        }
        
        try {
            await axios.delete(`http://localhost:5000/api/private-transfers/${transferId}`, {
                headers: { 'x-auth-token': token },
            });
            showPopup('Transfer canceled successfully!');
            fetchMyBookings();
        } catch (err) {
            showPopup(err.response.data.msg || 'Failed to cancel transfer.');
        }
    };

    const calculateCartTotal = () => {
        return bookings.reduce((total, item) => total + item.totalPrice, 0);
    };

    if (loading) {
        return <div className="text-center loading-message">Loading your bookings...</div>;
    }

    if (error) {
        return <div className="text-center error-message">{error}</div>;
    }

    return (
        <div className="container shopping-cart-container">
            <h2 className="cart-title">My Bookings (Shopping Cart)</h2>

            {bookings.length === 0 ? (
                <div className="empty-cart-message">
                    <p>Your cart is empty. Start exploring rooms and transfers!</p>
                </div>
            ) : (
                <div className="cart-layout">
                    <div className="cart-items-list">
                        {bookings.map(item => (
                            <div key={item._id} className="cart-item-card card">
                                {item.type === 'room' ? (
                                    <>
                                        <img src={item.roomId?.image} alt={item.roomId?.title} className="cart-item-image" />
                                        <div className="cart-item-details">
                                            <h4 className="cart-item-title">{item.roomId?.title}</h4>
                                            <p><strong>Type:</strong> Room Booking</p>
                                            <p><strong>Check-in:</strong> {new Date(item.checkInDate).toLocaleDateString()}</p>
                                            <p><strong>Check-out:</strong> {new Date(item.checkOutDate).toLocaleDateString()}</p>
                                            <p className="cart-item-price">BDT {item.totalPrice}</p>
                                            <div className="cart-item-actions">
                                                <button 
                                                    className="btn-cancel-item"
                                                    onClick={() => handleCancelBooking(item._id)}
                                                >
                                                    Cancel Room
                                                </button>
                                                <button 
                                                    className="btn-review-item"
                                                    onClick={() => setShowReviewForm(item.roomId?._id)}
                                                >
                                                    Write Review
                                                </button>
                                            </div>
                                            {showReviewForm === item.roomId?._id && (
                                                <ReviewForm 
                                                    roomId={item.roomId?._id} 
                                                    onReviewSubmitted={() => onReviewSubmitted(item.roomId?._id)} 
                                                />
                                            )}
                                            {reviews[item.roomId?._id] && (
                                                <div className="reviews-section">
                                                    <h5>Your Review:</h5>
                                                    {reviews[item.roomId._id].length > 0 ? (
                                                        reviews[item.roomId._id].map(review => (
                                                            <div key={review._id} className="review-item">
                                                                <p><strong>{review.userId.username}</strong> rated it {review.rating}/5</p>
                                                                <p>"{review.comment}"</p>
                                                            </div>
                                                        ))
                                                    ) : (
                                                        <p>No reviews yet.</p>
                                                    )}
                                                </div>
                                            )}
                                        </div>
                                    </>
                                ) : (
                                    <div className="cart-item-details transfer-item">
                                        <h4 className="cart-item-title">Private Transfer</h4>
                                        <p><strong>Type:</strong> {item.transferType}</p>
                                        <p><strong>Arrival Date:</strong> {new Date(item.arrivalDate).toLocaleDateString()}</p>
                                        <p><strong>Arrival Time:</strong> {item.arrivalTime}</p>
                                        <p className="cart-item-price">BDT {item.totalPrice}</p>
                                        <div className="cart-item-actions">
                                            <button 
                                                className="btn-cancel-item"
                                                onClick={() => handleCancelTransfer(item._id)}
                                            >
                                                Cancel Transfer
                                            </button>
                                        </div>
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>

                    <div className="cart-summary card">
                        <h3>Cart Summary</h3>
                        <div className="summary-line">
                            <span>Total Items:</span>
                            <span>{bookings.length}</span>
                        </div>
                        <div className="summary-line total-due">
                            <span>Total Due:</span>
                            <span>BDT {calculateCartTotal()}</span>
                        </div>
                        <button className="btn btn-primary checkout-btn">Proceed to Checkout</button>
                    </div>
                </div>
            )}
            {isPopupVisible && (
                <div className="popup-container">
                    <div className="popup-message">{popupMessage}</div>
                </div>
            )}
        </div>
    );
};

export default MyBookings;
