// client/src/App.js
// ... (existing imports)
import AddRoom from './components/AddRoom'; // Import the new component

// ... (existing PrivateRoute and App component)
function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));

  return (
    <Router>
      <div className="main-container">
        <Routes>
          <Route path="/" element={<Auth setIsAuthenticated={setIsAuthenticated} />} />
          <Route path="/home" element={<PrivateRoute><HomePage /></PrivateRoute>} />
          <Route path="/auth" element={<Auth setIsAuthenticated={setIsAuthenticated} />} />
          <Route path="/add-room" element={<PrivateRoute><AddRoom /></PrivateRoute>} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
