// client/src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import HomePage from './pages/HomePage';
import HotelInfo from './pages/HotelInfo';
import Auth from './components/Auth';
import AddRoom from './components/AddRoom';
import RoomList from './components/RoomList';
import BookingForm from './components/BookingForm';
import MyBookings from './components/MyBookings';
import './App.css'; 

const PrivateRoute = ({ children }) => {
  const isAuthenticated = localStorage.getItem('token');
  return isAuthenticated ? children : <Auth />;
};

// Create a simple placeholder component for now
const Transportation = () => {
  return <div style={{ padding: '50px', textAlign: 'center' }}>
    <h1>Transportation Services</h1>
    <p>This is where you will add transportation details.</p>
  </div>;
};

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));

  return (
    <Router>
      <div className="main-container">
        <Routes>
          <Route path="/" element={<Auth setIsAuthenticated={setIsAuthenticated} />} />
          <Route path="/home" element={<PrivateRoute><HomePage /></PrivateRoute>} />
          <Route path="/auth" element={<Auth setIsAuthenticated={setIsAuthenticated} />} />
          <Route path="/add-room" element={<PrivateRoute><AddRoom /></PrivateRoute>} />
          <Route path="/rooms" element={<RoomList />} />
          <Route path="/book/:id" element={<PrivateRoute><BookingForm /></PrivateRoute>} />
          <Route path="/mybookings" element={<PrivateRoute><MyBookings /></PrivateRoute>} />
          <Route path="/hotel" element={<HotelInfo />} />
          <Route path="/transportation" element={<Transportation />} /> {/* New route */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
